import React, { useState } from 'react';
import './styles.css'; 
import bossteam from './assets/bossteam.png';

const Game = () => {
    // State untuk menyimpan kata start dan end
    const [startWord, setStartWord] = useState("");
    const [endWord, setEndWord] = useState("");

    // Fungsi untuk menukar start word dan end word
    const handleToggle = () => {
        const temp = startWord;
        setStartWord(endWord);
        setEndWord(temp);
    };

    return (
        <div className="game-container">
            <img src={bossteam} alt="Boss Team" className="top-image" />
            <div className="input-container">
                <input
                    type="text"
                    className="textbox"
                    placeholder="Start Word"
                    value={startWord}
                    onChange={(e) => setStartWord(e.target.value)}
                />
                <button className="toggle-button" onClick={handleToggle}> 
                    â‡† 
                </button>
                <input
                    type="text"
                    className="textbox"
                    placeholder="End Word"
                    value={endWord}
                    onChange={(e) => setEndWord(e.target.value)}
                />
            </div>
            <div className="centered-container"> 
                <div className="dropdown-container"> 
                    <label htmlFor="algorithm">Choose Algorithm:   </label> 
                    <select id="algorithm" className="dropdown">
                        <option value="algorithm1">Uniform-Cost Search</option> 
                        <option value="algorithm2">Greedy Best-First Search</option>
                        <option value="algorithm3">A*</option>
                    </select>
                </div>
                <button className="search-button">Search</button> 
            </div>
            <div className="results-container"> 
            </div>
        </div>
    );
};

export default Game;
