import React, { useState } from 'react';
import './styles.css'; 

const Game = () => {
    // State untuk menyimpan kata start dan end
    const [startWord, setStartWord] = useState("");
    const [endWord, setEndWord] = useState("");

    // Fungsi untuk menukar start word dan end word
    const handleToggle = () => {
        const temp = startWord;
        setStartWord(endWord);
        setEndWord(temp);
    };

    return (
        <div className="game-container">
            <div className="input-container">
                <input
                    type="text"
                    className="textbox"
                    placeholder="Start Word" 
                />
                <div
                    className={`toggle-switch ${toggle ? 'right' : 'left'}`} 
                    onClick={handleToggle}
                >
                    {toggle ? 'On' : 'Off'} 
                </div>
                <input
                    type="text"
                    className="textbox"
                    placeholder="End Word" 
                />
            </div>
            <div className="dropdown-container">
                <label htmlFor="algorithm">Choose Algorithm:</label> 
                <select id="algorithm" className="dropdown"> 
                    <option value="algorithm1">Algorithm UCS</option> 
                    <option value="algorithm2">Algorithm Greedy Best First Search</option>
                    <option value="algorithm3">Algorithm A*</option>
                </select>
            </div>
            <button className="search-button">Search</button> {/* Tombol untuk mencari hasil */}

            <div className="results-container"> {/* Container berwarna biru untuk hasil */}
                {/* Area untuk hasil pencarian */}
            </div>
        </div>
    );
};

export default Game;
