import React from 'react';
import './styles.css'; 
import azul from './assets/azul.png'; 

const AboutMe = () => {
    return (
        <div className="about-me-container">
            <h1 className="about-me-title">About Me</h1> {/* Judul besar di tengah */}

            <div className="photocard"> {/* Photo card dengan gambar dan detail */}
                <h2 className="photocard-title">Azul</h2> {/* Judul photo card */}
                <img src={azul} alt="Azul" className="photocard-image" /> {/* Gambar Azul */}
                <div className="photocard-info"> {/* Informasi di bawah foto */}
                    <p>Nama  : Marzuli Suhada M</p>
                    <p>NIM   : 13522070</p>
                </div>
            </div>
        </div>
    );
};

export default AboutMe;
