import React, { useState } from 'react';
import './styles.css'; 

const Game = () => {
    const [toggle, setToggle] = useState(false);

    // Fungsi untuk toggle switch
    const handleToggle = () => {
        setToggle(!toggle); // Mengubah nilai toggle
    };

    return (
        <div className="game-container">
            <div className="input-container">
                <input
                    type="text"
                    className="textbox"
                    placeholder="Start Word" // Textbox pertama untuk kata awal
                />
                <div
                    className={`toggle-switch ${toggle ? 'right' : 'left'}`} // Toggle switch dengan kondisi kiri atau kanan
                    onClick={handleToggle}
                >
                    {toggle ? 'On' : 'Off'} // Teks pada toggle switch
                </div>
                <input
                    type="text"
                    className="textbox"
                    placeholder="End Word" // Textbox kedua untuk kata akhir
                />
            </div>
            <div className="dropdown-container">
                <label htmlFor="algorithm">Choose Algorithm:</label> {/* Label untuk dropdown */}
                <select id="algorithm" className="dropdown"> {/* Dropdown untuk memilih algoritma */}
                    <option value="algorithm1">Algorithm 1</option> 
                    <option value="algorithm2">Algorithm 2</option>
                    <option value="algorithm3">Algorithm 3</option>
                </select>
            </div>
            <button className="search-button">Search</button> {/* Tombol untuk mencari hasil */}

            <div className="results-container"> {/* Container berwarna biru untuk hasil */}
                {/* Area untuk hasil pencarian */}
            </div>
        </div>
    );
};

export default Game;
