import React, { useState } from 'react';
import './styles.css'; 

const Game = () => {
    // State untuk menyimpan kata start dan end
    const [startWord, setStartWord] = useState("");
    const [endWord, setEndWord] = useState("");

    // Fungsi untuk menukar start word dan end word
    const handleToggle = () => {
        const temp = startWord;
        setStartWord(endWord);
        setEndWord(temp);
    };

    return (
        <div className="game-container">
            <div className="input-container">
                <input
                    type="text"
                    className="textbox"
                    placeholder="Start Word"
                    value={startWord}
                    onChange={(e) => setStartWord(e.target.value)}
                />
                <button className="toggle-button" onClick={handleToggle}> 
                    â‡† 
                </button>
                <input
                    type="text"
                    className="textbox"
                    placeholder="End Word"
                    value={endWord}
                    onChange={(e) => setEndWord(e.target.value)}
                />
            </div>
            <div className="centered-container"> {/* Kontainer tengah */}
                <div className="dropdown-container"> {/* Menempatkan label di atas dropdown */}
                    <label htmlFor="algorithm">Choose Algorithm:</label> 
                    <select id="algorithm" className="dropdown"> {/* Dropdown untuk memilih algoritma */}
                        <option value="algorithm1">Algorithm 1</option> 
                        <option value="algorithm2">Algorithm 2</option>
                        <option value="algorithm3">Algorithm 3</option>
                    </select>
                </div>
                <button className="search-button">Search</button> {/* Tombol di tengah */}
            </div>
            <div className="results-container"> {/* Container untuk hasil */}
                {/* Area untuk hasil pencarian */}
            </div>
        </div>
    );
};

export default Game;
